import React, { useState, useEffect } from 'react';
import { getQuestions } from '../../api/api';
import QuestionForm from './QuestionForm';
import QuestionList from './QuestionList';

const AdminPage = () => {
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);

    const fetchQuestions = async () => {
        try {
            setLoading(true);
            const response = await getQuestions();
            setQuestions(response.data);
            setError('');
        } catch (err) {
            setError('Ошибка при загрузке вопросов');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchQuestions();
    }, []);

    const handleQuestionAdded = () => {
        fetchQuestions();
        setShowForm(false);
    };

    return (
        <div className="container">
            <h1>Панель администратора</h1>

            {error && <div className="error card">{error}</div>}

            <div className="admin-actions" style={{ marginBottom: '20px' }}>
                <button
                    className="btn"
                    onClick={() => setShowForm(!showForm)}
                >
                    {showForm ? 'Скрыть форму' : 'Добавить новый вопрос'}
                </button>
            </div>

            {showForm && (
                <QuestionForm onQuestionAdded={handleQuestionAdded} />
            )}

            {loading ? (
                <div>Загрузка вопросов...</div>
            ) : (
                <QuestionList questions={questions} />
            )}
        </div>
    );
};

export default AdminPage;
